IF DB_ID('FoodDeliveryDB') IS NULL
    CREATE DATABASE FoodDeliveryDB;
GO
USE FoodDeliveryDB;
GO

-- Tables
CREATE TABLE TaiKhoan (
    MaTK NVARCHAR(20) PRIMARY KEY,
    TenDangNhap NVARCHAR(50) NOT NULL,
    MatKhau NVARCHAR(200) NOT NULL,
    VaiTro NVARCHAR(20) NOT NULL,
    TrangThai BIT DEFAULT 1
);
GO

CREATE TABLE KhachHang (
    MaKH NVARCHAR(20) PRIMARY KEY,
    TenKH NVARCHAR(100),
    SDT NVARCHAR(20),
    DiaChi NVARCHAR(300),
    MaTK NVARCHAR(20) FOREIGN KEY REFERENCES TaiKhoan(MaTK)
);
GO

CREATE TABLE Shipper (
    MaShipper NVARCHAR(20) PRIMARY KEY,
    TenShipper NVARCHAR(100),
    SDT NVARCHAR(20),
    BienSoXe NVARCHAR(50),
    MaTK NVARCHAR(20) FOREIGN KEY REFERENCES TaiKhoan(MaTK)
);
GO

CREATE TABLE LoaiMonAn (
    MaLoai NVARCHAR(20) PRIMARY KEY,
    TenLoai NVARCHAR(100)
);
GO

CREATE TABLE NhaHang (
    MaNH NVARCHAR(10) PRIMARY KEY,
    TenNH NVARCHAR(200),
    DiaChi NVARCHAR(300),
    SDT NVARCHAR(20),
    MaTK NVARCHAR(20) FOREIGN KEY REFERENCES TaiKhoan(MaTK)
);
GO

CREATE TABLE MonAn (
    MaMon NVARCHAR(20) PRIMARY KEY,
    MaNH NVARCHAR(10) FOREIGN KEY REFERENCES NhaHang(MaNH),
    TenMon NVARCHAR(200),
    MaLoai NVARCHAR(20) FOREIGN KEY REFERENCES LoaiMonAn(MaLoai),
    Gia DECIMAL(12,2),
    MoTa NVARCHAR(500)
);
GO

CREATE TABLE DonHang (
    MaDon NVARCHAR(30) PRIMARY KEY,
    MaKH NVARCHAR(20) FOREIGN KEY REFERENCES KhachHang(MaKH),
    MaNH NVARCHAR(10) FOREIGN KEY REFERENCES NhaHang(MaNH),
    MaShipper NVARCHAR(20) NULL FOREIGN KEY REFERENCES Shipper(MaShipper),
    TrangThai NVARCHAR(50),
    TongTien DECIMAL(12,2),
    ThoiGianDat DATETIME DEFAULT SYSUTCDATETIME()
);
GO

CREATE TABLE ChiTietDonHang (
    MaDon NVARCHAR(30) FOREIGN KEY REFERENCES DonHang(MaDon),
    MaMon NVARCHAR(20) FOREIGN KEY REFERENCES MonAn(MaMon),
    SoLuong INT,
    DonGia DECIMAL(12,2),
    PRIMARY KEY (MaDon, MaMon)
);
GO

CREATE TABLE DanhGia (
    MaDanhGia INT IDENTITY(1,1) PRIMARY KEY,
    MaDon NVARCHAR(30) FOREIGN KEY REFERENCES DonHang(MaDon),
    Diem INT,
    BinhLuan NVARCHAR(500)
);
GO
